import React from 'react';
import { View, Text, StyleSheet, TouchableOpacity } from 'react-native';
import { SafeAreaBox } from "../../../components";
import { useRoute } from '@react-navigation/native';
import { HomeStackParamList } from '../../../screens/types';
import { RouteProp } from '@react-navigation/native';
import Icon from 'react-native-vector-icons/Feather';

type TopicsScreenRouteProp = RouteProp<HomeStackParamList, 'TopicsScreen'>;

export function TopicsScreen() {
  const route = useRoute<TopicsScreenRouteProp>();
  const { topicId } = route.params;

  return (
    <SafeAreaBox>
      <View style={styles.cardContainer}>
        <View style={styles.card}>
          <View style={styles.cardHeader}>
            <Text style={styles.topicTitle}>Education</Text>
            <View style={styles.cardActions}>
              <TouchableOpacity style={styles.iconButton}>
                <Icon name="edit-2" size={20} color="#4A90E2" />
              </TouchableOpacity>
              <TouchableOpacity style={styles.iconButton}>
                <Icon name="trash-2" size={20} color="#4A90E2" />
              </TouchableOpacity>
            </View>
          </View>
          
          <Text style={styles.wordText}>Physically</Text>
          
          <TouchableOpacity style={styles.practiceButton}>
            <Text style={styles.practiceButtonText}>Nhấp vào để tập thể</Text>
            <Icon name="play" size={20} color="#fff" />
          </TouchableOpacity>
        </View>

        <View style={styles.navigation}>
          <TouchableOpacity style={styles.navButton}>
            <Icon name="chevron-left" size={24} color="#000" />
          </TouchableOpacity>
          <Text style={styles.pageIndicator}>1 / 30</Text>
          <TouchableOpacity style={styles.navButton}>
            <Icon name="chevron-right" size={24} color="#000" />
          </TouchableOpacity>
        </View>
      </View>
    </SafeAreaBox>
  );
}

const styles = StyleSheet.create({
  header: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    backgroundColor: '#4A90E2',
    padding: 15,
  },
  headerTitle: {
    color: '#fff',
    fontSize: 18,
    fontWeight: 'bold',
  },
  cardContainer: {
    flex: 1,
    padding: 20,
  },
  card: {
    backgroundColor: '#fff',
    borderRadius: 10,
    padding: 20,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,
  },
  cardHeader: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: 20,
  },
  topicTitle: {
    fontSize: 16,
    color: '#333',
  },
  cardActions: {
    flexDirection: 'row',
  },
  iconButton: {
    marginLeft: 15,
  },
  wordText: {
    fontSize: 24,
    fontWeight: 'bold',
    textAlign: 'center',
    marginVertical: 30,
  },
  practiceButton: {
    backgroundColor: '#4A90E2',
    flexDirection: 'row',
    justifyContent: 'center',
    alignItems: 'center',
    padding: 15,
    borderRadius: 25,
    marginTop: 20,
  },
  practiceButtonText: {
    color: '#fff',
    marginRight: 10,
  },
  navigation: {
    flexDirection: 'row',
    justifyContent: 'center',
    alignItems: 'center',
    marginTop: 20,
  },
  navButton: {
    padding: 10,
  },
  pageIndicator: {
    marginHorizontal: 20,
    fontSize: 16,
  },
});
