import React, { useState } from 'react';
import {
  Modal,
  View,
  Text,
  TextInput,
  TouchableOpacity,
  ScrollView,
  StyleSheet,
  Alert,
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';

interface VocabWord {
  term: string;
  definition: string;
}

interface AddTopicModalProps {
  visible: boolean;
  onClose: () => void;
  onSave: (topicName: string, description: string, words: VocabWord[]) => void;
}

export function AddTopicModal({ visible, onClose, onSave }: AddTopicModalProps) {
  const [topicName, setTopicName] = useState('');
  const [description, setDescription] = useState('');
  const [currentTerm, setCurrentTerm] = useState('');
  const [currentDefinition, setCurrentDefinition] = useState('');
  const [words, setWords] = useState<VocabWord[]>([]);

  const handleAddWord = () => {
    if (!currentTerm.trim() || !currentDefinition.trim()) {
      Alert.alert('Lỗi', 'Vui lòng nhập cả từ vựng và nghĩa của từ');
      return;
    }

    const isDuplicate = words.some(
      word => word.term.toLowerCase() === currentTerm.toLowerCase()
    );

    if (isDuplicate) {
      Alert.alert('Lỗi', 'Từ vựng này đã tồn tại trong danh sách');
      return;
    }

    setWords([...words, { term: currentTerm, definition: currentDefinition }]);
    setCurrentTerm('');
    setCurrentDefinition('');
  };

  const handleDeleteWord = (index: number) => {
    setWords(words.filter((_, i) => i !== index));
  };

  const handleSave = () => {
    if (!topicName.trim()) {
      Alert.alert('Lỗi', 'Vui lòng nhập tên chủ đề');
      return;
    }

    if (words.length === 0) {
      Alert.alert('Lỗi', 'Vui lòng thêm ít nhất một từ vựng');
      return;
    }

    onSave(topicName, description, words);
    resetForm();
  };

  const resetForm = () => {
    setTopicName('');
    setDescription('');
    setCurrentTerm('');
    setCurrentDefinition('');
    setWords([]);
  };

  return (
    <Modal
      visible={visible}
      animationType="slide"
      transparent={true}
      onRequestClose={onClose}
    >
      <View style={styles.modalContainer}>
        <View style={styles.modalContent}>
          <View style={styles.header}>
            <Text style={styles.headerText}>Tạo Topic Mới</Text>
            <TouchableOpacity onPress={onClose} style={styles.closeButton}>
              <Ionicons name="close" size={24} color="#000" />
            </TouchableOpacity>
          </View>

          <ScrollView style={styles.scrollContent}>
            <TextInput
              style={styles.input}
              placeholder="Tên chủ đề"
              value={topicName}
              onChangeText={setTopicName}
            />

            <TextInput
              style={[styles.input, styles.descriptionInput]}
              placeholder="Mô tả (không bắt buộc)"
              value={description}
              onChangeText={setDescription}
              multiline
            />

            <View style={styles.wordInputContainer}>
              <TextInput
                style={[styles.input, styles.wordInput]}
                placeholder="Nhập từ vựng"
                value={currentTerm}
                onChangeText={setCurrentTerm}
              />
              <TextInput
                style={[styles.input, styles.wordInput]}
                placeholder="Nhập nghĩa"
                value={currentDefinition}
                onChangeText={setCurrentDefinition}
              />
              <TouchableOpacity 
                style={styles.addWordButton}
                onPress={handleAddWord}
              >
                <Ionicons name="add-circle" size={24} color="#4A90E2" />
              </TouchableOpacity>
            </View>

            <View style={styles.wordList}>
              {words.map((word, index) => (
                <View key={index} style={styles.wordItem}>
                  <View style={styles.wordContent}>
                    <Text style={styles.wordText}>{word.term}</Text>
                    <Text style={styles.definitionText}>{word.definition}</Text>
                  </View>
                  <TouchableOpacity
                    onPress={() => handleDeleteWord(index)}
                    style={styles.deleteButton}
                  >
                    <Ionicons name="trash-outline" size={20} color="#FF0000" />
                  </TouchableOpacity>
                </View>
              ))}
            </View>
          </ScrollView>

          <View style={styles.footer}>
            <TouchableOpacity
              style={[styles.button, styles.cancelButton]}
              onPress={onClose}
            >
              <Text style={styles.buttonText}>Hủy</Text>
            </TouchableOpacity>
            <TouchableOpacity
              style={[styles.button, styles.saveButton]}
              onPress={handleSave}
            >
              <Text style={[styles.buttonText, styles.saveButtonText]}>
                Lưu Topic
              </Text>
            </TouchableOpacity>
          </View>
        </View>
      </View>
    </Modal>
  );
}

const styles = StyleSheet.create({
  modalContainer: {
    flex: 1,
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
    justifyContent: 'center',
    alignItems: 'center',
  },
  modalContent: {
    width: '90%',
    maxHeight: '80%',
    backgroundColor: 'white',
    borderRadius: 20,
    overflow: 'hidden',
  },
  header: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: 15,
    borderBottomWidth: 1,
    borderBottomColor: '#E8E8E8',
  },
  headerText: {
    fontSize: 18,
    fontWeight: 'bold',
  },
  closeButton: {
    padding: 5,
  },
  scrollContent: {
    padding: 15,
  },
  input: {
    borderWidth: 1,
    borderColor: '#E8E8E8',
    borderRadius: 8,
    padding: 10,
    marginBottom: 10,
  },
  descriptionInput: {
    height: 80,
    textAlignVertical: 'top',
  },
  wordInputContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 10,
  },
  wordInput: {
    flex: 1,
    marginRight: 10,
    marginBottom: 0,
  },
  addWordButton: {
    padding: 5,
  },
  wordList: {
    marginTop: 10,
  },
  wordItem: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: 10,
    backgroundColor: '#F8F9FA',
    borderRadius: 8,
    marginBottom: 8,
  },
  wordContent: {
    flex: 1,
  },
  wordText: {
    fontSize: 16,
    fontWeight: '500',
  },
  definitionText: {
    fontSize: 14,
    color: '#666',
    marginTop: 4,
  },
  deleteButton: {
    padding: 5,
  },
  footer: {
    flexDirection: 'row',
    justifyContent: 'flex-end',
    padding: 15,
    borderTopWidth: 1,
    borderTopColor: '#E8E8E8',
  },
  button: {
    paddingVertical: 10,
    paddingHorizontal: 20,
    borderRadius: 8,
    marginLeft: 10,
  },
  cancelButton: {
    backgroundColor: '#F0F0F0',
  },
  saveButton: {
    backgroundColor: '#4A90E2',
  },
  buttonText: {
    fontSize: 16,
    fontWeight: '500',
  },
  saveButtonText: {
    color: 'white',
  },
});
