import React, { useState, useRef } from 'react';
import { 
  View, 
  Text, 
  StyleSheet, 
  TouchableOpacity, 
  Animated, 
  Dimensions
} from 'react-native';
import { SafeAreaBox } from "../../../components";
import { useRoute } from '@react-navigation/native';
import { HomeStackParamList } from '../../../screens/types';
import { RouteProp } from '@react-navigation/native';
import Icon from 'react-native-vector-icons/Feather';
import { getVocabByTopic } from '../../../data/Vocab';

type TopicsScreenRouteProp = RouteProp<HomeStackParamList, 'TopicsScreen'>;

export function TopicsScreen() {
  const route = useRoute<TopicsScreenRouteProp>();
  const { topicId } = route.params;
  
  // Add debug logs
  console.log('Received Topic ID:', topicId);
  
  const topicData = getVocabByTopic(topicId);
  console.log('Found Topic Data:', topicData);
  console.log('Topic Data Structure:', JSON.stringify(topicData, null, 2));
  
  const [currentWordIndex, setCurrentWordIndex] = useState(0);
  const [isFlipped, setIsFlipped] = useState(false);
  
  // Ensure we have data before accessing words
  const words = topicData?.words || [];
  const currentWord = words[currentWordIndex];

  // Add early return if no data
  if (!topicData || words.length === 0) {
    return (
      <SafeAreaBox>
        <View style={styles.container}>
          <Text style={styles.noDataText}>
            Không tìm thấy từ vựng cho chủ đề: {topicId}
          </Text>
          <Text style={[styles.noDataText, { marginTop: 10 }]}>
            Vui lòng kiểm tra lại ID chủ đề hoặc dữ liệu từ vựng
          </Text>
        </View>
      </SafeAreaBox>
    );
  }

  // Animation
  const flipAnimation = useRef(new Animated.Value(0)).current;

  const flipCard = () => {
    setIsFlipped(!isFlipped);
    Animated.spring(flipAnimation, {
      toValue: isFlipped ? 0 : 180,
      friction: 8,
      tension: 10,
      useNativeDriver: true,
    }).start();
  };

  const frontInterpolate = flipAnimation.interpolate({
    inputRange: [0, 180],
    outputRange: ['0deg', '180deg'],
  });

  const backInterpolate = flipAnimation.interpolate({
    inputRange: [0, 180],
    outputRange: ['180deg', '360deg'],
  });

  const frontAnimatedStyle = {
    transform: [{ rotateY: frontInterpolate }],
  };
  
  const backAnimatedStyle = {
    transform: [{ rotateY: backInterpolate }],
  };

  const handleNext = () => {
    if (currentWordIndex < words.length - 1) {
      setIsFlipped(false);
      flipAnimation.setValue(0);
      setCurrentWordIndex(currentWordIndex + 1);
    }
  };

  const handlePrevious = () => {
    if (currentWordIndex > 0) {
      setIsFlipped(false);
      flipAnimation.setValue(0);
      setCurrentWordIndex(currentWordIndex - 1);
    }
  };

  return (
    <SafeAreaBox>
      <View style={styles.container}>
        <TouchableOpacity onPress={flipCard} activeOpacity={1}>
          <View style={styles.cardContainer}>
            <Animated.View style={[styles.flashcard, frontAnimatedStyle]}>
              <Text style={styles.wordText}>{currentWord?.word}</Text>
              <Text style={styles.pronunciationText}>{currentWord?.pronunciation}</Text>
              <Text style={styles.partOfSpeechText}>{currentWord?.partOfSpeech}</Text>
            </Animated.View>
            
            <Animated.View style={[styles.flashcard, styles.flashcardBack, backAnimatedStyle]}>
              <Text style={styles.vietnameseText}>{currentWord?.vietnamese}</Text>
              <View style={styles.exampleContainer}>
                <Text style={styles.exampleText}>{currentWord?.example}</Text>
              </View>
            </Animated.View>
          </View>
        </TouchableOpacity>

        <View style={styles.navigation}>
          <TouchableOpacity 
            style={styles.navButton}
            onPress={handlePrevious}
            disabled={currentWordIndex === 0}
          >
            <Icon name="chevron-left" size={24} color={currentWordIndex === 0 ? "#ccc" : "#000"} />
          </TouchableOpacity>
          <Text style={styles.pageIndicator}>{`${currentWordIndex + 1} / ${words.length}`}</Text>
          <TouchableOpacity 
            style={styles.navButton}
            onPress={handleNext}
            disabled={currentWordIndex === words.length - 1}
          >
            <Icon name="chevron-right" size={24} color={currentWordIndex === words.length - 1 ? "#ccc" : "#000"} />
          </TouchableOpacity>
        </View>

        <Text style={styles.tapHint}>Tap card to flip</Text>
      </View>
    </SafeAreaBox>
  );
}

const { width, height } = Dimensions.get('window');
const cardWidth = width * 0.9;
const cardHeight = height * 0.6;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    paddingTop: 10,
  },
  topicTitle: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 30,
    color: '#333',
  },
  cardContainer: {
    width: cardWidth,
    height: cardHeight,
    marginVertical: 10,
  },
  flashcard: {
    width: '100%',
    height: '100%',
    backgroundColor: '#fff',
    borderRadius: 20,
    padding: 20,
    alignItems: 'center',
    justifyContent: 'center',
    backfaceVisibility: 'hidden',
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,
    position: 'absolute',
  },
  flashcardBack: {
    backgroundColor: '#fff',
    borderRadius: 20,
    padding: 20,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,
  },
  wordText: {
    fontSize: 32,
    fontWeight: 'bold',
    textAlign: 'center',
    marginVertical: 30,
  },
  pronunciationText: {
    fontSize: 16,
    color: '#666',
    marginVertical: 10,
  },
  partOfSpeechText: {
    fontSize: 16,
    color: '#666',
    marginVertical: 10,
  },
  vietnameseText: {
    fontSize: 28,
    fontWeight: 'bold',
    textAlign: 'center',
    marginVertical: 30,
  },
  exampleContainer: {
    marginTop: 20,
  },
  exampleText: {
    fontSize: 16,
    color: '#666',
  },
  navigation: {
    flexDirection: 'row',
    justifyContent: 'center',
    alignItems: 'center',
    marginTop: 20,
  },
  navButton: {
    padding: 10,
  },
  pageIndicator: {
    marginHorizontal: 20,
    fontSize: 16,
  },
  tapHint: {
    fontSize: 14,
    color: '#666',
    marginTop: 20,
  },
  noDataText: {
    fontSize: 16,
    color: '#666',
    textAlign: 'center',
    marginTop: 20,
  },
});
