import { View, Text, TextInput, TouchableOpacity, FlatList, StyleSheet } from "react-native";
import { SafeAreaBox } from "../../components";
import React, { useState, useEffect } from "react";
import { useNavigation } from "@react-navigation/native";
import type { NativeStackNavigationProp } from "@react-navigation/native-stack";
import { HomeStackParamList, Topic } from "../types";
import { getDBConnection, getAllTopics } from "../../database/db-service";

export function VocabularyScreen() {
  const navigation = useNavigation<NativeStackNavigationProp<HomeStackParamList>>();
  const [topics, setTopics] = useState<Topic[]>([]);

  useEffect(() => {
    const loadTopics = async () => {
      try {
        const db = await getDBConnection();
        const topicsData = await getAllTopics(db) as Topic[];
        setTopics(topicsData);
      } catch (error) {
        console.error("Error loading topics:", error);
      }
    };
    loadTopics();
  }, []);

  // H√†m x√≥a ch·ªß ƒë·ªÅ
  const handleDelete = (id: string) => {
    setTopics(topics.filter(topic => topic.TopicID !== id));
  };

  const handleAddPress = () => {
    try {
      console.log('Navigating to MyLibraryScreen');
      navigation.navigate('MyLibraryScreen');
    } catch (error) {
      console.error('Navigation error:', error);
    }
  };

  return (
    <SafeAreaBox>
      {/* Ti√™u ƒë·ªÅ */}
      <View style={styles.header}>
        <Text style={styles.headerText}>Th∆∞ vi·ªán c·ªßa t√¥i</Text>
      </View>

      {/* Thanh t√¨m ki·∫øm */}
      <View style={styles.searchContainer}>
        <TextInput
          placeholder="Nh·∫≠p ch·ªß ƒë·ªÅ c·∫ßn t√¨m ki·∫øm"
          style={styles.searchInput}
          placeholderTextColor="#666"
        />
      </View>

      {/* Danh s√°ch c√°c ch·ªß ƒë·ªÅ */}
      <FlatList
        data={topics}
        keyExtractor={(item) => item.TopicID}
        renderItem={({ item }) => (
          <View style={styles.card}>
            <View style={styles.cardContent}>
              <Text style={styles.title}>{item.TopicName}</Text>
              <Text style={styles.termCount}>{item.Description}</Text>
            </View>
            <View style={styles.cardActions}>
              <TouchableOpacity>
                <Text>‚úèÔ∏è</Text>
              </TouchableOpacity>
              <TouchableOpacity onPress={() => handleDelete(item.TopicID)}>
                <Text>üóëÔ∏è</Text>
              </TouchableOpacity>
            </View>
          </View>
        )}
      />

      {/* N√∫t th√™m */}
      <TouchableOpacity style={styles.addButton} onPress={handleAddPress}>
        <Text style={styles.addButtonText}>+</Text>
      </TouchableOpacity>
    </SafeAreaBox>
  );
}

const styles = StyleSheet.create({
  header: {
    backgroundColor: "#4A90E2",
    padding: 16,
    borderRadius: 8,
    marginBottom: 16,
    alignItems: "center",
  },
  headerText: {
    color: "#fff",
    fontSize: 18,
    fontWeight: "bold",
  },
  searchContainer: {
    flexDirection: "row",
    marginHorizontal: 16,
    marginBottom: 16,
    borderWidth: 1,
    borderColor: "#E8E8E8",
    borderRadius: 25,
    backgroundColor: "#F6F6F6",
    alignItems: "center",
    paddingLeft: 16,
    paddingRight: 8,
    height: 40,
  },
  searchInput: {
    flex: 1,
    fontSize: 14,
    color: "#000",
    padding: 0,
    outlineStyle: 'none',
  },
  searchButton: {
    padding: 8,
  },
  searchIcon: {
    fontSize: 16,
  },
  card: {
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
    backgroundColor: "#e6e6fa",
    padding: 16,
    marginBottom: 8,
    borderRadius: 8,
    shadowColor: "#000",
    shadowOpacity: 0.1,
    shadowOffset: { width: 0, height: 1 },
  },
  cardContent: {
    flex: 1,
  },
  termCount: {
    fontSize: 14,
    color: "#666",
  },
  title: {
    fontSize: 16,
    fontWeight: "bold",
    marginTop: 4,
  },
  cardActions: {
    flexDirection: "row",
    justifyContent: "space-between",
    width: 50,
  },
  addButton: {
    backgroundColor: "#4A90E2",
    height: 50,
    width: 50,
    borderRadius: 25,
    justifyContent: "center",
    alignItems: "center",
    alignSelf: "center",
    marginTop: 16,
  },
  addButtonText: {
    color: "#fff",
    fontSize: 24,
    fontWeight: "bold",
  },
});
