import { View, Text, TextInput, TouchableOpacity, FlatList, StyleSheet, Image } from "react-native";
import { SafeAreaBox } from "../../components";
import React, { useState, useEffect } from "react";
import { useNavigation } from "@react-navigation/native";
import type { NativeStackNavigationProp } from "@react-navigation/native-stack";
import { HomeStackParamList, Topic } from "../types";
import { getDBConnection, getAllTopics, createTables, insertTopics } from "../../database/db-service";

type TopicImage = {
  source: any;
  alt: string;
};

interface TopicWithImage extends Topic {
  image: TopicImage;
}

export function VocabularyScreen() {
  const navigation = useNavigation<NativeStackNavigationProp<HomeStackParamList>>();
  const [topics, setTopics] = useState<Topic[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  const sampleTopics: TopicWithImage[] = [
    {
      TopicID: 'T1',
      TopicName: 'Family',
      Description: 'Từ vựng về gia đình và các mối quan hệ',
      image: {
        source: require('../../../assets/family_topic.jpg'),
        alt: 'Family Topic'
      }
    },
    {
      TopicID: 'T2',
      TopicName: 'Food',
      Description: 'Từ vựng về đồ ăn và thức uống',
      image: {
        source: require('../../../assets/food_topic.jpg'),
        alt: 'Food Topic'
      }
    },
    {
      TopicID: 'T3',
      TopicName: 'Travel',
      Description: 'Từ vựng về du lịch và phương tiện đi lại',
      image: {
        source: require('../../../assets/travel_topic.jpg'),
        alt: 'Travel Topic'
      }
    }
  ];

  return (
    <SafeAreaBox>
      <View style={styles.header}>
        <Text style={styles.headerText}>Thư viện từ vựng</Text>
      </View>

      <View style={styles.searchContainer}>
        <TextInput
          placeholder="Tìm kiếm chủ đề..."
          style={styles.searchInput}
          placeholderTextColor="#666"
        />
      </View>

      {isLoading ? (
        <View style={styles.centerContent}>
          <Text>Đang tải...</Text>
        </View>
      ) : error ? (
        <View style={styles.centerContent}>
          <Text style={styles.errorText}>{error}</Text>
        </View>
      ) : (
        <View style={styles.rootContainer}>
          <FlatList
            numColumns={2}
            data={sampleTopics}
            renderItem={({ item }) => (
              <TouchableOpacity 
                style={styles.topicCard}
                onPress={() => navigation.navigate('TopicDetailScreen', { topicId: item.TopicID })}
              >
                <View style={styles.imageContainer}>
                  <Image 
                    source={item.image.source}
                    style={styles.topicImage}
                    resizeMode="cover"
                  />
                </View>
                <View style={styles.cardContent}>
                  <Text style={styles.topicTitle}>{item.TopicName}</Text>
                  <Text style={styles.topicDescription} numberOfLines={2}>
                    {item.Description}
                  </Text>
                </View>
              </TouchableOpacity>
            )}
            keyExtractor={(item) => item.TopicID}
          />
        </View>
      )}

      <TouchableOpacity style={styles.addButton} onPress={handleAddPress}>
        <Text style={styles.addButtonText}>+</Text>
      </TouchableOpacity>
    </SafeAreaBox>
  );
}

const styles = StyleSheet.create({
  header: {
    backgroundColor: "#4A90E2",
    padding: 16,
    borderRadius: 8,
    marginBottom: 16,
    alignItems: "center",
  },
  headerText: {
    color: "#fff",
    fontSize: 18,
    fontWeight: "bold",
  },
  searchContainer: {
    flexDirection: "row",
    marginHorizontal: 16,
    marginBottom: 16,
    borderWidth: 1,
    borderColor: "#E8E8E8",
    borderRadius: 25,
    backgroundColor: "#F6F6F6",
    alignItems: "center",
    paddingLeft: 16,
    paddingRight: 8,
    height: 40,
  },
  searchInput: {
    flex: 1,
    fontSize: 14,
    color: "#000",
    padding: 0,
    outlineStyle: 'none',
  },
  searchButton: {
    padding: 8,
  },
  searchIcon: {
    fontSize: 16,
  },
  card: {
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
    backgroundColor: "#e6e6fa",
    padding: 16,
    marginBottom: 8,
    borderRadius: 8,
    shadowColor: "#000",
    shadowOpacity: 0.1,
    shadowOffset: { width: 0, height: 1 },
  },
  cardContent: {
    flex: 1,
  },
  termCount: {
    fontSize: 14,
    color: "#666",
  },
  title: {
    fontSize: 16,
    fontWeight: "bold",
    marginTop: 4,
  },
  cardActions: {
    flexDirection: "row",
    justifyContent: "space-between",
    width: 50,
  },
  addButton: {
    backgroundColor: "#4A90E2",
    height: 50,
    width: 50,
    borderRadius: 25,
    justifyContent: "center",
    alignItems: "center",
    alignSelf: "center",
    marginTop: 16,
  },
  addButtonText: {
    color: "#fff",
    fontSize: 24,
    fontWeight: "bold",
  },
  centerContent: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  errorText: {
    color: 'red',
    textAlign: 'center',
  },
  rootContainer: {
    padding: 12,
    flex: 1,
  },
  topicCard: {
    flex: 1,
    margin: 8,
    backgroundColor: '#ffffff',
    borderRadius: 12,
    overflow: 'hidden',
    elevation: 3,
    shadowColor: "#000",
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.1,
    shadowRadius: 4,
    maxWidth: '47%',
  },
  imageContainer: {
    width: '100%',
    height: 120,
  },
  topicImage: {
    width: '100%',
    height: '100%',
  },
  cardContent: {
    padding: 12,
  },
  topicTitle: {
    fontSize: 16,
    fontWeight: 'bold',
    color: '#333',
    marginBottom: 4,
    textAlign: 'center',
  },
  topicDescription: {
    fontSize: 12,
    color: '#666',
    textAlign: 'center',
  },
  addButton: {
    position: 'absolute',
    right: 20,
    bottom: 20,
    backgroundColor: '#4A90E2',
    height: 56,
    width: 56,
    borderRadius: 28,
    justifyContent: 'center',
    alignItems: 'center',
    elevation: 4,
    shadowColor: "#000",
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.25,
    shadowRadius: 4,
  },
  addButtonText: {
    color: '#fff',
    fontSize: 28,
    fontWeight: 'bold',
  },
});
