import React, { useRef } from 'react';
import { View, Text, StyleSheet, ScrollView, TouchableOpacity, TextInput, Animated } from 'react-native';
import { SafeAreaBox } from "../../../../components";
import { useRoute } from '@react-navigation/native';
import { HomeStackParamList, MyLibraryScreenProps } from '../../../types';
import { RouteProp } from '@react-navigation/native';
import { PanGestureHandler, State } from 'react-native-gesture-handler';

type MyLibraryScreenRouteProp = RouteProp<HomeStackParamList, 'MyLibraryScreen'>;

export function MyLibraryScreen({ navigation }: MyLibraryScreenProps) {
  const route = useRoute<MyLibraryScreenRouteProp>();
  const [title, setTitle] = React.useState('');
  const [description, setDescription] = React.useState('');
  const [vocabularyList, setVocabularyList] = React.useState<{ id: number; term: string; definition: string; animatedValue: Animated.Value }[]>([
    { id: 1, term: '', definition: '', animatedValue: new Animated.Value(0) },
    { id: 2, term: '', definition: '', animatedValue: new Animated.Value(0) },
    { id: 3, term: '', definition: '', animatedValue: new Animated.Value(0) },
  ]);
  const scrollViewRef = useRef<ScrollView>(null);
  const itemWidth = 300;

  const addVocabularyItem = () => {
    const newItem = { id: vocabularyList.length + 1, term: '', definition: '', animatedValue: new Animated.Value(0) };
    setVocabularyList([...vocabularyList, newItem]);
    scrollViewRef.current?.scrollToEnd({ animated: true });
  };

  const deleteVocabularyItem = (id: number) => {
    setVocabularyList(vocabularyList.filter(item => item.id !== id));
  };

  const onGestureEvent = (event: any, id: number, animatedValue: Animated.Value) => {
    if (event.nativeEvent.state === State.ACTIVE) {
      const translationX = event.nativeEvent.translationX;
      if (translationX < 0 && Math.abs(translationX) <= itemWidth) {
        animatedValue.setValue(translationX);
      }
    } else if (event.nativeEvent.state === State.END) {
      const translationX = event.nativeEvent.translationX;
      if (translationX < -itemWidth * (1 / 3)) {
        deleteVocabularyItem(id);
      } else {
        Animated.timing(animatedValue, {
          toValue: 0,
          duration: 300,
          useNativeDriver: true,
        }).start();
      }
    }
  };

  return (
    <SafeAreaBox>
      <ScrollView ref={scrollViewRef} style={styles.container}>
        <TextInput
          style={styles.titleInput}
          placeholder="Nhập tiêu đề, ví dụ: 'Animal and plant'"
          value={title}
          onChangeText={setTitle}
        />
        
        <TextInput
          style={styles.descriptionInput}
          placeholder="Thêm mô tả..."
          value={description}
          onChangeText={setDescription}
          multiline
        />

        {vocabularyList.map((item) => (
          <PanGestureHandler onGestureEvent={(event) => onGestureEvent(event, item.id, item.animatedValue)} key={item.id}>
            <Animated.View style={[styles.vocabularyItem, { transform: [{ translateX: item.animatedValue }] }]}>
              <View style={styles.inputContainer}>
                <TextInput
                  style={styles.termInput}
                  placeholder=""
                  value={item.term}
                  onChangeText={(text) => {
                    setVocabularyList(vocabularyList.map(vocabItem => 
                      vocabItem.id === item.id ? { ...vocabItem, term: text } : vocabItem
                    ));
                  }}
                />
                <Text style={styles.helperText}>Thuật ngữ</Text>
                <TextInput
                  style={styles.definitionInput}
                  placeholder=""
                  value={item.definition}
                  onChangeText={(text) => {
                    setVocabularyList(vocabularyList.map(vocabItem => 
                      vocabItem.id === item.id ? { ...vocabItem, definition: text } : vocabItem
                    ));
                  }}
                />
                <Text style={styles.helperText}>Định nghĩa</Text>
              </View>
              {item.animatedValue.interpolate({
                inputRange: [-itemWidth, 0],
                outputRange: [1, 0],
                extrapolate: 'clamp',
              }) < -itemWidth * (1 / 3) && (
                <TouchableOpacity style={styles.deleteButton} onPress={() => deleteVocabularyItem(item.id)}>
                  <Text style={styles.deleteButtonText}>🗑️</Text>
                </TouchableOpacity>
              )}
            </Animated.View>
          </PanGestureHandler>
        ))}

        <TouchableOpacity style={styles.addButton} onPress={addVocabularyItem}>
          <Text style={styles.addButtonText}>+</Text>
        </TouchableOpacity>
      </ScrollView>
    </SafeAreaBox>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 16,
    backgroundColor: '#f0f4ff',
  },
  header: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 16,
    textAlign: 'center',
  },
  titleInput: {
    borderWidth: 1,
    borderColor: '#ccc',
    borderRadius: 8,
    padding: 12,
    marginBottom: 16,
  },
  descriptionInput: {
    borderWidth: 1,
    borderColor: '#ccc',
    borderRadius: 8,
    padding: 12,
    height: 100,
    marginBottom: 16,
  },
  vocabularyItem: {
    marginBottom: 16,
  },
  inputContainer: {
    borderWidth: 1,
    borderColor: '#ccc',
    padding: 8,
    marginBottom: 8,
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.3,
    elevation: 5,
  },
  termInput: {
    borderBottomWidth: 2,
    borderColor: '#000',
    padding: 8,
    marginBottom: 2,
    fontWeight: 'bold',
  },
  definitionInput: {
    borderBottomWidth: 2,
    borderColor: '#000',
    padding: 8,
    marginBottom: 4,
  },
  helperText: {
    fontSize: 12,
    color: '#666',
    marginTop: 4,
    textAlign: 'left',
    fontWeight: 'bold',
  },
  addButton: {
    width: 50,
    height: 50,
    borderRadius: 25,
    backgroundColor: '#4A90E2',
    justifyContent: 'center',
    alignItems: 'center',
    alignSelf: 'center',
    marginTop: 16,
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.3,
    shadowRadius: 4,
    elevation: 5,
  },
  addButtonText: {
    color: 'white',
    fontSize: 24,
  },
  deleteButton: {
    position: 'absolute',
    right: 10,
    top: 10,
    backgroundColor: 'transparent',
  },
  deleteButtonText: {
    fontSize: 24,
  },
});
