import * as SQLite from 'expo-sqlite';
import { Platform } from 'react-native';

export const getDBConnection = async () => {
    try {
      // Nếu đang chạy trên web, trả về dữ liệu mẫu thay vì throw error
      if (Platform.OS === 'web') {
        console.log('Running on web platform - using mock data');
        return {
          transaction: (callback: any) => {
            callback({
              executeSql: (_query: string, _params: any[], successCallback: any) => {
                // Trả về dữ liệu mẫu cho topics
                const mockTopics = [
                  { TopicID: 'T1', TopicName: 'Gia đình', Description: 'Từ vựng về gia đình và các mối quan hệ' },
                  { TopicID: 'T2', TopicName: 'Ẩm thực', Description: 'Từ vựng về đồ ăn và thức uống' },
                  { TopicID: 'T3', TopicName: 'Du lịch', Description: 'Từ vựng về du lịch và phương tiện đi lại' },
                ];
                successCallback(null, { rows: { _array: mockTopics } });
              }
            });
          }
        } as any;
      }
      
      const db = SQLite.openDatabase('Toeic-data.db');
      if (!db) {
        throw new Error('Không thể mở cơ sở dữ liệu');
      }
      return db;
    } catch (error) {
      console.error('Lỗi khi mở cơ sở dữ liệu:', error);
      throw error;
    }
};

export const createTables = async (db: SQLite.SQLiteDatabase) => {
    return new Promise((resolve, reject) => {
        db.transaction(
            tx => {
                // Tạo bảng Tests
                tx.executeSql(
                    `CREATE TABLE IF NOT EXISTS Tests (
                        TestID TEXT PRIMARY KEY,
                        Title TEXT NOT NULL
                    );`,
                    []
                );

                // Tạo bảng Parts
                tx.executeSql(
                    `CREATE TABLE IF NOT EXISTS Parts (
                        PartID TEXT PRIMARY KEY,
                        TestID TEXT,
                        PartNumber INTEGER NOT NULL,
                        FOREIGN KEY (TestID) REFERENCES Tests(TestID)
                    );`,
                    []
                );

                // Tạo bảng Questions
                tx.executeSql(
                    `CREATE TABLE IF NOT EXISTS Questions (
                        QuestionID TEXT PRIMARY KEY,
                        PartID TEXT,
                        QuestionNumber INTEGER NOT NULL,
                        QuestionText TEXT,
                        ImagePath TEXT,
                        AudioPath TEXT,
                        CorrectAnswer TEXT,
                        FOREIGN KEY (PartID) REFERENCES Parts(PartID)
                    );`,
                    []
                );

                // Tạo bảng Topics (Chủ đề)
                tx.executeSql(
                    `CREATE TABLE IF NOT EXISTS Topics (
                        TopicID TEXT PRIMARY KEY,
                        TopicName TEXT NOT NULL,
                        Description TEXT,
                        ImagePath TEXT
                    );`,
                    []
                );

                // Tạo bảng Vocabulary (Từ vựng)
                tx.executeSql(
                    `CREATE TABLE IF NOT EXISTS Vocabulary (
                        VocabID TEXT PRIMARY KEY,
                        TopicID TEXT,
                        Word TEXT NOT NULL,
                        Meaning TEXT NOT NULL,
                        Example TEXT,
                        Pronunciation TEXT,
                        WordType TEXT,
                        ImagePath TEXT,
                        FOREIGN KEY (TopicID) REFERENCES Topics(TopicID)
                    );`,
                    []
                );
            },
            (error) => {
                console.error('Error creating tables:', error);
                reject(error);
            },
            () => {
                console.log('All tables created successfully');
                resolve(true);
            }
        );
    });
};

// tao danh sach cac bai test
export const insertTests = async (db: SQLite.SQLiteDatabase) => {
    return new Promise((resolve, reject) => {
        db.transaction(tx => {
            // Insert 10 bài test
            for (let i = 1; i <= 10; i++) {
                tx.executeSql(
                    'INSERT OR IGNORE INTO Tests (TestID, Title) VALUES (?, ?)',
                    [i.toString(), `SpeakingTest${i}`],
                    (_, result) => {
                        if (i === 10) {
                            console.log('All tests inserted successfully');
                        }
                    }
                );
            }
        },
        (error) => {
            console.error('Error inserting tests:', error);
            reject(error);
        },
        () => {
            console.log('Tests transaction completed');
            resolve(true);
        });
    });
};

// tao danh sach cac phan trong bai test
export const insertParts = async (db: SQLite.SQLiteDatabase) => {
    return new Promise((resolve, reject) => {
        db.transaction(tx => {
            // Lặp qua 10 bài test
            for (let testId = 1; testId <= 10; testId++) {
                // Lặp qua 5 phần cho mỗi bài test
                for (let partNumber = 1; partNumber <= 5; partNumber++) {
                    const partId = `${testId}_${partNumber}`; // Tạo PartID duy nhất
                    tx.executeSql(
                        'INSERT OR IGNORE INTO Parts (PartID, TestID, PartNumber) VALUES (?, ?, ?)',
                        [partId, testId.toString(), partNumber],
                        (_, result) => {
                            if (testId === 10 && partNumber === 5) {
                                console.log('All parts inserted successfully');
                            }
                        }
                    );
                }
            }
        },
        (error) => {
            console.error('Error inserting parts:', error);
            reject(error);
        },
        () => {
            console.log('Parts transaction completed');
            resolve(true);
        });
    });
};

// Hàm để lấy tất cả tests
export const getAllTests = async (db: SQLite.SQLiteDatabase) => {
    return new Promise((resolve, reject) => {
        db.transaction(tx => {
            tx.executeSql(
                'SELECT * FROM Tests ORDER BY TestID',
                [],
                (_, { rows: { _array } }) => {
                    resolve(_array);
                },
                (_, error) => {
                    reject(error);
                    return false;
                }
            );
        });
    });
};

// Hàm để lấy tất cả parts của một test
export const getPartsForTest = async (db: SQLite.SQLiteDatabase, testId: string) => {
    return new Promise((resolve, reject) => {
        db.transaction(tx => {
            tx.executeSql(
                'SELECT * FROM Parts WHERE TestID = ? ORDER BY PartNumber',
                [testId],
                (_, { rows: { _array } }) => {
                    resolve(_array);
                },
                (_, error) => {
                    reject(error);
                    return false;
                }
            );
        });
    });
};

export const getAllTopics = async (db: SQLite.SQLiteDatabase) => {
  return new Promise((resolve, reject) => {
    db.transaction(tx => {
      tx.executeSql(
        'SELECT * FROM Topics ORDER BY TopicID',
        [],
        (_, { rows: { _array } }) => resolve(_array),
        (_, error) => {
          reject(error);
          return false;
        }
      );
    });
  });
};

export const insertTopics = async (db: SQLite.SQLiteDatabase) => {
  return new Promise((resolve, reject) => {
    db.transaction(tx => {
      const topics = [
        ['T1', 'Gia đình', 'Từ vựng về gia đình và các mối quan hệ'],
        ['T2', 'Ẩm thực', 'Từ vựng về đồ ăn và thức uống'],
        ['T3', 'Du lịch', 'Từ vựng về du lịch và phương tiện đi lại']
      ];
      topics.forEach(([id, name, description]) => {
        tx.executeSql(
          'INSERT OR IGNORE INTO Topics (TopicID, TopicName, Description) VALUES (?, ?, ?)',
          [id, name, description]
        );
      });
    }, reject, () => resolve(true));
  });
};
